import React, { useState } from 'react';
import "../src/css/contact.css";
import Navbar from "./components/navbar";
import Footer from "./components/footer";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    subject: '',
    message: ''
  });



  // State to track touched status of each field
  const [touchedFields, setTouchedFields] = useState({
    name: false,
    phone: false,
    email: false,
    subject: false,
    message: false
  });

  const getBorderColor = (field) => {
    const isEmpty = formData[field].trim() === '';
    let isValidInput = true;

    switch (field) {
      case 'name':
        isValidInput = /^[A-Za-z]+$/.test(formData[field]);
        break;
      case 'phone':
        isValidInput = /^[0-9+]+$/.test(formData[field]);
        break;
      case 'email':
        isValidInput = formData[field].includes('@');
        break;
      default:
        break;
    }

    const borderColor = touchedFields[field] ? (isEmpty || !isValidInput ? 'red' : 'green') : 'transparent';
    console.log(`Field: ${field}, IsEmpty: ${isEmpty}, IsValidInput: ${isValidInput}, BorderColor: ${borderColor}`);
    return borderColor;
  };
  const handleChange = (e) => {
    const { name, value } = e.target;

    // Mark the field as touched
    setTouchedFields({ ...touchedFields, [name]: true });

    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate form fields
    const isValid = Object.entries(formData).every(([key, value]) => {
      switch (key) {
        case 'name':
          return /^[A-Za-z]{1,15}$/.test(value);
        case 'phone':
          return /^[0-9+]{1,13}$/.test(value);
        case 'email':
          return value.includes('@');
        case 'subject':
          // Allow up to 20 characters for 'subject'
          return value.length <= 20;
        case 'message':
          // Allow up to 40 characters for 'message'
          return value.length <= 40;
        default:
          return true;
      }
    });
  };

  return (
    <>
      <Navbar />
      <main>
        <div id="formcontact">
        <form
      id="contactForm"
      onsubmit="validateForm(event)"
      action="https://formspree.io/f/xwkdywrd"
      method="POST"
      name="myform"
    >
            <div className="containercont">
              <h1>Reach Out to Us. </h1>
              <h1>We Are Just a Message Away!</h1>
              <br />
              <div className="contact-box">
                <div className="contact">
                  <h2>Write Your Request</h2>
                  <br />
                  <label htmlFor="name">Name:</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    onChange={handleChange}
                    style={{ borderColor: getBorderColor('name') }}
                    aria-label="Name"
                    required/>

                  <label htmlFor="phone">Phone:</label>
                  <input
                    id="phone"
                    type="text"
                    name="phone"
                    onChange={handleChange}
                    placeholder="000 000 0000"
                    style={{ borderColor: getBorderColor('phone') }}
                    aria-label="Phone"
                    required/>

                  <label htmlFor="email">Email:</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    onChange={handleChange}
                    style={{ borderColor: getBorderColor('email') }}
                    aria-label="Email"
                    required/>

                  <label htmlFor="subject">Subject:</label>
                  <input
                    type="text"
                    name="subject"
                    onChange={handleChange}
                    style={{ borderColor: getBorderColor('subject') }}
                    aria-label="Subject"
                    required/>

                  <label htmlFor="message">Message:</label>
                  <textarea
                    id="message"
                    name="message"
                    onChange={handleChange}
                    rows={4}
                    style={{ borderColor: getBorderColor('message') }}
                    aria-label="Message"
                    defaultValue={""}
                    required/>
                  <button className="send" id="contact" type="Submit">
                    SEND
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </main>
      <Footer />
    </>
  );
}








