import React, { useState, useRef, useEffect } from 'react';
import Dron from "../image/dron.jpeg";
import Camera from "../image/camera.jpg";
import Headphones from "../image/headphones.jpg";
import "../css/carousel.css";

export default function Carousel() {
  const sliderRef = useRef(null);
  const [currentSlide, setCurrentSlide] = useState(1);

  useEffect(() => {
    const intervalId = setInterval(() => {
      // Move to the next slide
      setCurrentSlide((prevSlide) => (prevSlide % 5) + 1);
    }, 4000);

    return () => clearInterval(intervalId);
  }, []);

  const handleHover = (direction) => {
    const scrollAmount = direction === 'next' ? 200 : -200;
    if (sliderRef.current) {
      sliderRef.current.scrollLeft += scrollAmount;
    }
  };

  return (
    <section id="slider" ref={sliderRef}>
      {[Camera, Dron, Headphones, Camera, Dron].map((image, index) => (
        <label key={`slide${index + 1}`} htmlFor={`s${index + 1}`} onMouseEnter={() => handleHover('prev')}>
          <input type="radio" name="slider" id={`s${index + 1}`} />
          <img src={image} alt={`Slide ${index + 1}`} />
        </label>
      ))}
    </section>
  );
}

